import React, { useState } from "react";

function Form() {
  const [addTodoInput, setAddTodoInput] = useState("");
  const [allTodo, setAllTodo] = useState([]);
  const [page, setPage] = useState("all");
  const [selectedTodo, setSelectedTodo] = useState("");
  const changeTodoInput = (e) => {
    setAddTodoInput(e.target.value);
  };

  const handleKeyDown = (event) => {
    if (event.key === "Enter") {
      setAddTodoInput("");
      setAllTodo([...allTodo, addTodoInput]);
    }
  };

  const handlePage = (event) => {
    setSelectedTodo(event.target.value);
  };

  return (
    <div>
      <h1>Todo List</h1>
      <input
        name="add_todo"
        value={addTodoInput}
        onChange={changeTodoInput}
        onKeyDown={handleKeyDown}
      ></input>

      {allTodo.map((todo, index) => (
        <div key={index}>
          <input type="radio" value={todo} name="todo" onChange={handlePage} />
          {todo}
        </div>
      ))}

      <div>
        <button onChange={setPage("all")}>All</button>
        <button onChange={setPage("active")}>Active</button>
        <button onChange={setPage("complated")}>Complated</button>
      </div>

      {selectedTodo}
    </div>
  );
}

export default Form;
