import React, { useState } from "react";

function Form() {
  const [addTodoInput, setAddTodoInput] = useState("");
  const [allTodo, setAllTodo] = useState([]);
  const [page, setPage] = useState("all");
  const changeTodoInput = (e) => {
    setAddTodoInput(e.target.value);
  };

  const handleKeyDown = (event) => {
    if (event.key === "Enter") {
      setAddTodoInput("");
      setAllTodo([...allTodo, addTodoInput]);
    }
  };
  return (
    <div>
      <h1>Todo List</h1>
      <input
        name="add_todo"
        value={addTodoInput}
        onChange={changeTodoInput}
        onKeyDown={handleKeyDown}
      ></input>

      {allTodo.map((i) => (
        <> <input type="radio" name={i} checked={false} />{i}</>

      ))}

      <div>
        <button onChange={setPage("all")}>All</button>
        <button onChange={setPage("active")}>Active</button>
        <button onChange={setPage("complated")}>Complated</button>
      </div>
    </div>
  );
}

export default Form;
